import React from 'react';
import Modal from 'react-modal';

const MessageModal = ({ isOpen, message, onConfirm, onClose }) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      contentLabel='Message Modal'
      style={{
        content: {
          top: '50%',
          left: '50%',
          right: 'auto',
          bottom: 'auto',
          marginRight: '-50%',
          transform: 'translate(-50%, -50%)',
          maxWidth: '400px', // Adjust the maximum width as per your requirement
          maxHeight: '80vh', // Adjust the maximum height as per your requirement
          overflow: 'auto', // Enable scrolling if the content overflows the modal
        },
      }}
    >
      <h2>Error</h2>
      <p>{message}</p>
      <button
        style={{
          backgroundColor: 'red',
        }}
        onClick={onClose}
      >
        <button onClick={onConfirm}>Yes</button>
        Close
      </button>
    </Modal>
  );
};

export default MessageModal;
