import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const navigate = useNavigate();

  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = async () => {
    if (email === '') {
      alert('Please enter a valid email');
    } else if (username === '') {
      alert('Please enter a valid username');
    } else if (password === '') {
      alert('Please enter a valid password');
    } else {
      // Assuming localStorage key for users is 'users'
      const existingUsers = JSON.parse(localStorage.getItem('users')) || [];

      // Check if the username already exists using a loop
      let userExists = false;
      for (const user of existingUsers) {
        if (user.email === email) {
          userExists = true;
          break;
        }
      }

      if (userExists) {
        alert('Email already exists. Please choose a different one.');
      } else {
        // Add the new user to the local storage
        const newUser = { email, username, password };
        existingUsers.push(newUser);
        localStorage.setItem('users', JSON.stringify(existingUsers));

        // Notify the user about successful registration
        alert('Registration successful!');
        navigate('/');
      }
    }
  };

  return (
    <form class='register-container'>
      <div>
        <h2>Admin Registration</h2>
        <div id='register'>
          <label htmlFor='mail'>Email</label>
          <input
            id='mail'
            type='email'
            placeholder='enter email'
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br></br>
          <label htmlFor='username'>Username</label>
          <input
            id='username'
            type='text'
            placeholder='enter username'
            value={username}
            required
            onChange={(e) => setUsername(e.target.value)}
          />
          <br></br>
          <label htmlFor='pass'>Password</label>
          <input
            id='pass'
            type='password'
            placeholder='enter password'
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button className='button muted-button' onClick={handleRegister}>
            Register
          </button>
        </div>
      </div>
    </form>
  );
};

export default Register;
