import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import MessageModal from '../Dashboard/message'; // Import the MessageModal component

const Register = () => {
  const navigate = useNavigate();

  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isMessageOpen, setIsMessageOpen] = useState(false);
  const [data, setData] = useState(false);

  const handleCloseMessage = () => {
    setIsMessageOpen(false);
  };

  const handleCancel = () => {
    navigate('/'); // Navigate to the login page
  };

  const handleRegister = (e) => {
    e.preventDefault(); // Prevent form submission default behavior

    if (email === '') {
      // setMessage('Please enter a valid email');
      // setIsMessageOpen(true);
    } else if (username === '') {
      // setMessage('Please enter a valid username');
      // setIsMessageOpen(true);
    } else if (password === '') {
      // setMessage('Please enter a valid password');
      // setIsMessageOpen(true);
    } else {
      // Assuming localStorage key for users is 'users'
      const existingUsers = JSON.parse(localStorage.getItem('users')) || [];

      // Check if the username already exists using a loop
      let userExists = false;
      for (const user of existingUsers) {
        if (user.email === email) {
          userExists = true;
          break;
        }
      }

      if (userExists) {
        setMessage('Email already exists. Please choose a different one.');
        setData(true);
        setIsMessageOpen(true);
      } else {
        // Add the new user to the local storage
        const newUser = { email, username, password };
        existingUsers.push(newUser);
        localStorage.setItem('users', JSON.stringify(existingUsers));
        setData(false);
        // Notify the user about successful registration
        setMessage('Registration successful!');
        setIsMessageOpen(true);
      }
    }
  };

  const handleOkMessage = () => {
    setIsMessageOpen(false);
    navigate('/');
  };

  return (
    <form className='register-container' onSubmit={handleRegister}>
      {' '}
      {/* Added onSubmit event */}
      <div>
        <h2>Admin Registration</h2>
        <div id='register'>
          <label htmlFor='mail'>Email</label>
          <input
            id='mail'
            type='email'
            placeholder='enter email'
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <br />
          <label htmlFor='username'>Username</label>
          <input
            id='username'
            type='text'
            placeholder='enter username'
            value={username}
            required
            onChange={(e) => setUsername(e.target.value)}
          />
          <br />
          <label htmlFor='pass'>Password</label>
          <input
            id='pass'
            type='password'
            placeholder='enter password'
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button className='button muted-button' type='submit'>
            {' '}
            {/* Added type="submit" */}
            Register
          </button>
          <button
            style={{
              backgroundColor: 'red',
              marginTop: '10px',
              color: 'white',
            }}
            className='button muted-button'
            onClick={handleCancel}
          >
            Cancel
          </button>
        </div>
      </div>
      <MessageModal
        isOpen={isMessageOpen}
        message={message}
        onConfirm={handleOkMessage}
        onClose={handleCloseMessage}
        data={data}
      />
    </form>
  );
};

export default Register;
