import React, { useState, useEffect } from 'react';
import { employeesData } from '../../data/employees';
import List from './List';
import Create from './create';
import Header from './Header';
import { useNavigate } from 'react-router-dom';
import MessageModal from '../Dashboard/message'; // Import the MessageModal component

const Dashboard = ({ setIsAuthenticated }) => {
  const [employees, setEmployees] = useState(employeesData);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [isAdding, setIsAdding] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [message, setMessage] = useState(''); // State for message content
  const [isMessageOpen, setIsMessageOpen] = useState(false); // State for message modal visibility
  const [confirmAction, setConfirmAction] = useState(null); // State for storing the action to confirm
  const [confirmData, setConfirmData] = useState(null); // State for storing the data related to the action
  const [data, setData] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedData = localStorage.getItem('employees_data');
    if (!storedData) {
      localStorage.setItem('employees_data', JSON.stringify(employeesData));
      setEmployees(employeesData);
    } else {
      const parsedData = JSON.parse(storedData);
      setEmployees(parsedData);
    }
  }, []);

  const handleEdit = (id) => {
    const [employee] = employees.filter((employee) => employee.id === id);

    setSelectedEmployee(employee);
    setIsEditing(true);
    navigate('/edit/' + id);
  };

  const handleDelete = (id) => {
    const employee = employees.find((employee) => employee.id === id);

    // Set the confirmation action and data
    setConfirmAction('delete');
    setConfirmData({ id, employee });
    setMessage(
      `Are you sure you want to delete ${employee.firstName} ${employee.lastName}'s data?`
    );
    setData(false);
    setIsMessageOpen(true);
  };

  const handleConfirmAction = () => {
    if (confirmAction === 'delete') {
      const { id, employee } = confirmData;
      const employeesCopy = employees.filter((emp) => emp.id !== id);
      localStorage.setItem('employees_data', JSON.stringify(employeesCopy));
      setEmployees(employeesCopy);

      setMessage(
        `Deleted! ${employee.firstName} ${employee.lastName}'s data has been deleted.`
      );
      setData(true);
      setIsMessageOpen(true); // Open the message modal
    }
    // Add more cases for other confirm actions if needed
    setConfirmAction(null); // Reset confirm action
    setConfirmData(null); // Reset confirm data
  };

  const handleCloseMessage = () => {
    setIsMessageOpen(false); // Close the message modal
  };

  return (
    <div className='container'>
      {!isAdding && !isEditing && (
        <>
          <Header
            setIsAdding={setIsAdding}
            setIsAuthenticated={setIsAuthenticated}
          />
          <List
            employees={employees}
            handleEdit={handleEdit}
            handleDelete={handleDelete}
          />
        </>
      )}
      {isAdding && (
        <Create
          employees={employees}
          setEmployees={setEmployees}
          setIsAdding={setIsAdding}
        />
      )}
      <MessageModal
        isOpen={isMessageOpen}
        message={message}
        onConfirm={handleConfirmAction} // Pass the confirm action handler
        onClose={handleCloseMessage}
        data={data}
      />
    </div>
  );
};

export default Dashboard;
