import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import emailjs from '@emailjs/browser';

const Create = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const navigate = useNavigate();
  const employees = JSON.parse(localStorage.getItem('employees_data'));
  const [isAdding, setIsAdding] = useState(false);

  console.log(employees);

  const handleAdd = async (e) => {
    e.preventDefault();

    if (!firstName || !lastName || !email) {
      alert('Error! All fields are required.');
      return;
    }

    const existingEmployee = employees.find((emp) => emp.email === email);
    if (existingEmployee) {
      alert('User is already registered with the same email');
      return; // Exit the function early if user already exists
    }
    const id = employees?.length + 1;
    const newEmployee = {
      id,
      firstName,
      lastName,
      email,
    };

    const updatedEmployees = [...employees, newEmployee];
    // localStorage.setItem('employees_data', JSON.stringify(updatedEmployees));
    // // setEmployees(updatedEmployees); // Uncomment if you use this state in your parent component
    // // setIsAdding(false); // Uncomment to close the form after adding an employee

    // alert(`Added! ${firstName} ${lastName}'s data has been added.`);
    // navigate('/dashboard');
    try {
      // Send email using EmailJS
      const templateParams = {
        to_name: `${firstName} ${lastName}`,
        subject: 'Welcome to the Company',
      };

      await emailjs.send(
        'service_qm7s8ga',
        'template_eibtv84',
        templateParams,
        email
      );

      alert(
        `Added! ${firstName} ${lastName}'s data has been added, and a welcome email has been sent.`
      );
      navigate('/dashboard');
    } catch (error) {
      console.error('Failed to send email', error);
      alert('Error! Failed to send email.');
    }
  };

  const handleCancel = () => {
    // Uncomment the following line to close the form without adding an employee
    setIsAdding(false);
    navigate('/dashboard');
  };

  return (
    <div className='small-container'>
      <form onSubmit={handleAdd}>
        <h1>Add Employee</h1>
        <label htmlFor='firstName'>First Name</label>
        <input
          id='firstName'
          type='text'
          name='firstName'
          value={firstName}
          required
          onChange={(e) => setFirstName(e.target.value)}
        />
        <label htmlFor='lastName'>Last Name</label>
        <input
          id='lastName'
          type='text'
          name='lastName'
          value={lastName}
          required
          onChange={(e) => setLastName(e.target.value)}
        />
        <label htmlFor='email'>Email</label>
        <input
          id='email'
          type='email'
          name='email'
          value={email}
          required
          onChange={(e) => setEmail(e.target.value)}
        />
        <div style={{ marginTop: '30px' }}>
          <input className='button muted-button' type='submit' value='Add' />
          <input
            className='button muted-button'
            type='button'
            value='Cancel'
            onClick={handleCancel}
          />
        </div>
      </form>
    </div>
  );
};

export default Create;
