  //   // Assuming localStorage key for users is 'users'
  //   const existingUsers = JSON.parse(localStorage.getItem('users')) || [];

  //   // Check if the username already exists using a loop
  //   let userExists = false;
  //   for (const user of existingUsers) {
  //     if (user.email === email) {
  //       userExists = true;
  //       break;
  //     }
  //   }

  //   if (userExists) {
