import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { FaEye, FaEyeSlash } from 'react-icons/fa';
import MessageModal from '../Dashboard/message';

const Login = ({ setIsAuthenticated }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState('');
  const [isMessageOpen, setIsMessageOpen] = useState(false);
  const navigate = useNavigate();

  const handlePasswordVisibilityToggle = () => {
    setShowPassword(!showPassword);
  };

  const handleCloseMessage = () => {
    setIsMessageOpen(false);
  };

  const handleOkMessage = () => {
    setIsMessageOpen(false);
  };

  const handleLogin = (e) => {
    e.preventDefault();

    const usersFromLocalStorage = JSON.parse(localStorage.getItem('users'));

    if (!usersFromLocalStorage || usersFromLocalStorage.length === 0) {
      setMessage('No users found. Please register first.');
      setIsMessageOpen(true);
      return;
    }

    let loginStatus = false;
    if (email === '') {
      setMessage('Please enter your email');
      setIsMessageOpen(true);
    } else if (password === '') {
      setMessage('Please enter your password');
      setIsMessageOpen(true);
    } else {
      for (var i = 0; i < usersFromLocalStorage.length; i++) {
        if (
          usersFromLocalStorage[i].email === email &&
          usersFromLocalStorage[i].password === password
        ) {
          localStorage.setItem('is_authenticated', true);
          loginStatus = true;
          setIsAuthenticated(true);
          setMessage('Successfully logged in!');
          setIsMessageOpen(true);
          navigate('/dashboard');
          break;
        }
      }

      if (!loginStatus) {
        setMessage('Error: Incorrect email or password.');
        setIsMessageOpen(true);
      }
    }
  };

  return (
    <div className='small-container'>
      <form onSubmit={handleLogin}>
        <h1>Admin Login</h1>
        <label htmlFor='email'>Email</label>
        <input
          id='email'
          type='email'
          name='email'
          required
          placeholder='enter email'
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <br />
        <label htmlFor='password'>Password</label>
        <div
          className={`password-input-container ${
            showPassword ? 'visible' : ''
          }`}
        >
          <input
            id='password'
            type={showPassword ? 'text' : 'password'}
            name='password'
            required
            placeholder='enter password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <div
            className='password-toggle-button'
            onClick={handlePasswordVisibilityToggle}
          >
            {showPassword ? <FaEye /> : <FaEyeSlash />}
          </div>
        </div>
        <input
          style={{ marginTop: '12px' }}
          type='submit'
          value='Login'
          onClick={handleLogin}
        />
      </form>
      <p>
        Not have an Account?{' '}
        <Link to='/register'>
          <button className='button muted-button'>Register Here</button>
        </Link>
      </p>
      <MessageModal
        isOpen={isMessageOpen}
        message={message}
        onClose={handleCloseMessage}
        onConfirm={handleOkMessage}
      />
    </div>
  );
};

export default Login;
