import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import emailjs from '@emailjs/browser';
import MessageModal from '../Dashboard/message'; // Import the MessageModal component

const Create = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const navigate = useNavigate();
  const employees = JSON.parse(localStorage.getItem('employees_data'));
  const [isAdding, setIsAdding] = useState(false);
  const [isMessageOpen, setIsMessageOpen] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    emailjs.init({
      publicKey: 'Vaa7zzUhJKzwPYlG0',
    });
  });

  const handleAdd = async (e) => {
    e.preventDefault();

    if (!firstName || !lastName || !email) {
      setMessage('Error! All fields are required.');
      setIsMessageOpen(true);
      return;
    }

    const existingEmployee = employees?.find((emp) => emp.email === email);
    if (existingEmployee) {
      setMessage('User is already registered with the same email');
      setIsMessageOpen(true);
      return; // Exit the function early if user already exists
    }
    const id = employees?.length + 1;
    const newEmployee = {
      id,
      firstName,
      lastName,
      email,
    };

    const updatedEmployees = [...employees, newEmployee];
    localStorage.setItem('employees_data', JSON.stringify(updatedEmployees));
    try {
      // Send email using EmailJS
      const templateParams = {
        to_name: `${firstName} ${lastName}`,
        email: email,
        subject: 'Welcome to the Company',
      };
      console.log(email);
      await emailjs.send(
        'service_qm7s8ga', // Replace with your EmailJS Service ID
        'template_eibtv84', // Replace with your EmailJS Template ID
        templateParams
      );

      setMessage(
        `Added! ${firstName} ${lastName}'s data has been added, and a welcome email has been sent.`
      );
      setIsMessageOpen(true);
      navigate('/dashboard');
    } catch (error) {
      console.error('Failed to send email', error);
      setMessage('Error! Failed to send email.');
      setIsMessageOpen(true);
    }
  };

  const handleCancel = () => {
    setIsMessageOpen(true);
  };

  const confirmCancel = () => {
    setIsAdding(false);
    navigate('/dashboard');
  };

  const cancelCancel = () => {
    setIsMessageOpen(false);
  };

  return (
    <div className='small-container'>
      <form onSubmit={handleAdd}>
        <h1>Add Employee</h1>
        <label htmlFor='firstName'>First Name</label>
        <input
          id='firstName'
          type='text'
          name='firstName'
          value={firstName}
          required
          onChange={(e) => setFirstName(e.target.value)}
        />
        <label htmlFor='lastName'>Last Name</label>
        <input
          id='lastName'
          type='text'
          name='lastName'
          value={lastName}
          required
          onChange={(e) => setLastName(e.target.value)}
        />
        <label htmlFor='email'>Email</label>
        <input
          id='email'
          type='email'
          name='email'
          value={email}
          required
          onChange={(e) => setEmail(e.target.value)}
        />
        <div style={{ marginTop: '30px' }}>
          <input className='button muted-button' type='submit' value='Add' />
          <input
            className='button muted-button'
            type='button'
            value='Cancel'
            onClick={handleCancel}
          />
        </div>
      </form>
      <MessageModal
        isOpen={isMessageOpen}
        message={message}
        onClose={() => setIsMessageOpen(false)}
      />
      {/* <MessageModal
        isOpen={isMessageOpen}
        message='Are you sure you want to cancel adding a new employee?'
        onConfirm={confirmCancel}
        onCancel={cancelCancel}
      /> */}
    </div>
  );
};

export default Create;
