import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

const EmployeeDetails = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    // Fetch employeesData from local storage
    const employeesData = JSON.parse(localStorage.getItem('employees_data'));
    // Find the employee with the matching id
    const selectedEmployee = employeesData.find((emp) => emp.id == id);

    // Update the state with the selected employee
    setEmployee(selectedEmployee);
  }, [id]);

  if (!employee) {
    return <p>No Employee Found....</p>; // You may want to show a loading message or redirect if the employee is not found.
  }

  return (
    <div className='employee-details'>
      <h2>Employee Details</h2>
      <p>
        <strong>EMP-ID:</strong> {employee.id}
      </p>
      <p>
        <strong>First Name:</strong> {employee.firstName}
      </p>
      <p>
        <strong>Last Name:</strong> {employee.lastName}
      </p>
      <p>
        <strong>Email:</strong> {employee.email}
      </p>

      {/* Add other details as needed */}

      <Link to='/dashboard'>
        <button className='button muted-button'>Close Details</button>
      </Link>
    </div>
  );
};

export default EmployeeDetails;
