import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const Update = () => {
  const { id } = useParams();
  console.log(id);

  // Initial state should be set when employee data is available
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');

  const [employee, setEmployee] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch employeesData from local storage
    const employeesData =
      JSON.parse(localStorage.getItem('employees_data')) || [];
    console.log(employeesData);
    // Find the employee with the matching id
    const selectedEmployee = employeesData.find((emp) => emp.id == id);
    console.log(selectedEmployee);
    // Update the state with the selected employee
    setEmployee(selectedEmployee);
  }, [id]);

  // Set initial state when employee data is available
  useEffect(() => {
    if (employee) {
      setFirstName(employee.firstName);
      setLastName(employee.lastName);
      setEmail(employee.email);
    }
  }, [employee]);

  const handleUpdate = (e) => {
    e.preventDefault();

    if (!firstName || !lastName || !email) {
      alert('Error!\nAll fields are required.');
      return;
    }

    const updatedEmployee = {
      id: parseInt(id),
      firstName,
      lastName,
      email,
    };

    // Update the employee in the local storage
    const updatedEmployees =
      JSON.parse(localStorage.getItem('employees_data')) || [];
    const index = updatedEmployees.findIndex((emp) => emp.id === parseInt(id));
    console.log('index', index);
    updatedEmployees.splice(index, 1, updatedEmployee);

    localStorage.setItem('employees_data', JSON.stringify(updatedEmployees));
    setEmployee(updatedEmployee);

    alert(
      `Success!\n${updatedEmployee.firstName} ${updatedEmployee.lastName}'s data has been updated.`
    );

    // Navigate to the dashboard after the update
    navigate('/dashboard');
  };

  if (!employee) {
    return <p>No Employee Found...</p>; // You may want to show a loading message or redirect if the employee is not found.
  }

  return (
    <div className='small-container'>
      <form onSubmit={handleUpdate}>
        <h1>Edit Employee</h1>
        <label htmlFor='firstName'>First Name</label>
        <input
          id='firstName'
          type='text'
          name='firstName'
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <label htmlFor='lastName'>Last Name</label>
        <input
          id='lastName'
          type='text'
          name='lastName'
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <label htmlFor='email'>Email</label>
        <input
          id='email'
          type='email'
          name='email'
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <div style={{ marginTop: '30px' }}>
          <button onClick={handleUpdate}>Save Changes</button>
          <button
            className='button muted-button'
            onClick={() => navigate(`/dashboard`)}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default Update;
