import React, { useState, useEffect } from 'react';

import { employeesData } from '../../data/employees';
import List from './List';
import Create from './create';
import Header from './Header';
import Update from './update';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ setIsAuthenticated }) => {
  const [employees, setEmployees] = useState(employeesData);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [isAdding, setIsAdding] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();
  console.log(employees);

  useEffect(() => {
    // Check if employees data is present in localStorage
    const storedData = localStorage.getItem('employees_data');

    // If data is not present, set the initial dummy data
    if (!storedData) {
      localStorage.setItem('employees_data', JSON.stringify(employeesData));
      setEmployees(employeesData);
    } else {
      // If data is present, parse and set it
      const parsedData = JSON.parse(storedData);
      setEmployees(parsedData);
    }
  }, []);

  const handleEdit = (id) => {
    const [employee] = employees.filter((employee) => employee.id === id);

    setSelectedEmployee(employee);
    setIsEditing(true);
    navigate('/edit/' + id);
  };

  const handleDelete = (id) => {
    const employee = employees.find((employee) => employee.id === id);

    if (
      window.confirm(
        `Are you sure you want to delete ${employee.firstName} ${employee.lastName}'s data?`
      )
    ) {
      const employeesCopy = employees.filter((employee) => employee.id !== id);
      localStorage.setItem('employees_data', JSON.stringify(employeesCopy));
      setEmployees(employeesCopy);

      alert(
        `Deleted! ${employee.firstName} ${employee.lastName}'s data has been deleted.`
      );
    }
  };

  return (
    <div className='container'>
      {!isAdding && !isEditing && (
        <>
          <Header
            setIsAdding={setIsAdding}
            setIsAuthenticated={setIsAuthenticated}
          />
          <List
            employees={employees}
            handleEdit={handleEdit}
            handleDelete={handleDelete}
          />
        </>
      )}
      {isAdding && (
        <Create
          employees={employees}
          setEmployees={setEmployees}
          setIsAdding={setIsAdding}
        />
      )}
    </div>
  );
};

export default Dashboard;
